# coding:utf-8import refile_object = open('ci_utf8.txt', 'r', encoding='utf-8')ci_dict = {}pattern = r',|\.|/|;|\'|`|\[|\]|<|>|\?|:|"|\{|\}|\~|!|@|#|\$|%|\^|&|\(|\)|-|=|\_|\+|，|。|、|；|‘|’|【|】|·|！| |…|（|）'try:    lines = file_object.readlines()finally:    file_object.close()for line in lines:    if line != '\n':        line = line[:-1]        if len(line) < 10:            ci_dict[line] = ci_dict.get(line, 0) + 1        else:            result_list = re.split(pattern, line)            for phrase in result_list:                size = len(phrase)                for index in range(size):                    ci_dict[phrase[index]] = ci_dict.get(phrase[index], 0) + 1                    if index < len(phrase)-1:                        ci_dict[phrase[index:index+2]] = ci_dict.get(phrase[index:index+2], 0) + 1try:    fp = open("out.txt", 'w')    # for v, k in ci_dict.items():    #     fp.writelines(v + ' ' + str(k))    # print([ v for v in sorted(ci_dict.values(), reverse= True)])    back_items = [[v[1], v[0]] for v in ci_dict.items()]    back_items.sort()    for v, k in back_items:        print(v, k)        fp.writelines(str(v) + ' ' + str(k) + '\n')finally:    fp.close()# for item in ci_dict:#     if len(item) > 1:#         print(item)